package system.rest;import java.io.Serializable;@SuppressWarnings("serial")public class ResultObject implements Serializable {	/*转换成对应的值*/	private Object resultObj;	/**	 * 返回是否成功的状态标志	 */	private Boolean status = Boolean.FALSE;	/*加密了标志*/	private Boolean encoding = Boolean.TRUE;	/*条件信息*/	private Object params;	public Object getResultObj() {		return resultObj;	}	public void setResultObj(Object resultObj) {		this.resultObj = resultObj;	}	public Boolean getStatus() {		return status;	}	public void setStatus(Boolean status) {		this.status = status;	}	public Boolean getEncoding() {		return encoding;	}	public void setEncoding(Boolean encoding) {		this.encoding = encoding;	}	public Object getParams() {		return params;	}	public void setParams(Object params) {		this.params = params;	}}